(ns website1.articles  (:require    [hiccup.core :as hc]    [hiccup.page :as hp]    [clojure.string :as cs]    [clojure.edn :as edn]))(def links  (list   [:link {:href "css/foundationmock.css", :type "text/css", :rel "stylesheet"}           [:link {:href "css/stylemock.css", :type "text/css", :rel "stylesheet"}]]          [:link {:type "text/css", :rel "stylesheet", :href "https://fonts.googleapis.com/css?family=Poiret+One"}]          [:link {:type "text/css", :rel "stylesheet", :href "https://fonts.googleapis.com/css?family=Montserrat:400"}]))(def articles  (->> (slurp (str "resources/public/articles.edn"))       (edn/read-string)))(defn taketitle [id]  (get-in articles [(dec id) :title]))(defn taketext [id]  (get-in articles [(dec id) :text]))(defn open  [id] (hp/html5 [:head links]                 [:body                  [:div {:class "row"}                   [:div {:class "large-12 columns headlines"}                    [:div {:class "headlines"} (:title (articles (dec 2)))]]]                  [:div {:class "row"}                   [:a {:class "tombol", :href "#"} (:text (articles (dec 2)))]]                  [:div {:class "footer2"}                   [:div {:class "row"}                    [:div {:class "large-12 columns"} " "                     [:h6 "Copyright stuffs."] " "]]]]))